UPDATE YIMIAO_ORDERDETAIL a INNER JOIN (
select #{val.yimiaoOrderdetailId} ORDERDETAIL_ID, #{val.returnReason} RETURN_REASON, #{val.lastPurchaseCount} TOTAL_RETURN_COUNT
) b ON a.ORDERDETAIL_ID = b.ORDERDETAIL_ID SET a.TOTAL_RETURN_COUNT = a.TOTAL_RETURN_COUNT+b.TOTAL_RETURN_COUNT, a.TOTAL_RETURN_AMOUNT = a.PURCHASE_PRICE*b.TOTAL_RETURN_COUNT + a.TOTAL_RETURN_AMOUNT, a.RETURN_REASON = b.RETURN_REASON

UPDATE yimiao_orderdetail a INNER JOIN ( SELECT ORDERDETAIL_ID,sum(TOTAL_RETURN_COUNT) TOTAL_RETURN_COUNT from yimiao_orderdetail_ret where ORDERDETAIL_RET_ID in
#{var.orderdetailRetId}
GROUP BY ORDERDETAIL_ID ) b on a.ORDERDETAIL_ID = b.ORDERDETAIL_ID SET a.TOTAL_RETURN_COUNT = a.TOTAL_RETURN_COUNT - b.TOTAL_RETURN_COUNT, a.TOTAL_RETURN_AMOUNT =a.TOTAL_RETURN_AMOUNT - (a.PURCHASE_PRICE*b.TOTAL_RETURN_COUNT)

UPDATE yimiao_orderdetail a INNER JOIN ( SELECT ORDERDETAIL_ID,sum(TOTAL_RETURN_COUNT) TOTAL_RETURN_COUNT from yimiao_orderdetail_ret where ORDERDETAIL_ID in
#{var.orderDetailId}
GROUP BY ORDERDETAIL_ID ) b on a.ORDERDETAIL_ID = b.ORDERDETAIL_ID SET a.TOTAL_RETURN_COUNT = b.TOTAL_RETURN_COUNT, a.TOTAL_RETURN_AMOUNT =a.PURCHASE_PRICE*b.TOTAL_RETURN_COUNT

