now() SUBMIT_TIME, null FILING_ID , null COMPANY_ID_PS , b.COMPANY_NAME_PS , b.BID_PRICE PURCHASE_PRICE , a.PURCHASE_COUNT , a.PURCHASE_AMOUNT , null IS_READ , null READ_USER_ID , null READ_USER_NAME , null READ_TIME , null CONFIRM_USER_ID , null CONFIRM_USER_NAME , null CONFIRM_TIME , null CONFIRM_STATE , null ORDERDETAIL_STATE , null REFUSE_REASON , null DETAIL_DISTRIBUTE_ADDRESS , 0 TOTAL_DISTRIBUTE_COUNT, 0 TOTAL_DISTRIBUTE_AMOUNT, 0 TOTAL_WAREHOUSE_COUNT, 0 TOTAL_WAREHOUSE_AMOUNT, 0 TOTAL_RETURN_AMOUNT, 0 TOTAL_RETURN_COUNT, 0 IS_USING, 0 COMP_RATIO , a.ORDER_CUSTOM_INFO , #{userId} ADD_USER_ID , #{name} ADD_USER_NAME , now() ADD_TIME , #{userId} LAST_UPDATE_USER_ID , #{name} LAST_UPDATE_USER_NAME , now() LAST_UPDATE_TIME, b.COMPANY_NAME_PS WT_COMPANY_NAME_PS from (
SELECT #{val.procurecatalogId} PROCURECATALOG_ID, #{val.lastPurchaseCount} PURCHASE_COUNT, #{val.purchaseAmount} PURCHASE_AMOUNT, #{val.remark} ORDER_CUSTOM_INFO
) a INNER JOIN yimiao_catalog b on a.PROCURECATALOG_ID = b.PROCURECATALOG_ID and b.HOSPITAL_ID = #{userId}

mapper.update:
UPDATE yimiao_orderdetail a INNER JOIN ( SELECT ORDERDETAIL_ID,sum(TOTAL_RETURN_COUNT) TOTAL_RETURN_COUNT from yimiao_orderdetail_ret where ORDER_ID in
#{var}
GROUP BY ORDERDETAIL_ID ) b on a.ORDERDETAIL_ID = b.ORDERDETAIL_ID SET a.TOTAL_RETURN_COUNT = a.TOTAL_RETURN_COUNT - b.TOTAL_RETURN_COUNT, a.TOTAL_RETURN_AMOUNT =a.TOTAL_RETURN_AMOUNT - (a.PURCHASE_PRICE*b.TOTAL_RETURN_COUNT)

UPDATE YIMIAO_ORDERDETAIL a INNER JOIN (
select #{val.orderdetailId} ORDERDETAIL_ID, #{val.confirmState} CONFIRM_STATE, #{val.refuseReason} REFUSE_REASON, #{val.companyNamePs} COMPANY_NAME_PS
) b on a.ORDERDETAIL_ID = b.ORDERDETAIL_ID SET a.CONFIRM_STATE = b.CONFIRM_STATE, a.REFUSE_REASON =b.REFUSE_REASON, a.COMPANY_NAME_PS =b.COMPANY_NAME_PS

UPDATE YIMIAO_ORDERDETAIL a INNER JOIN (
select #{val.orderdetailId} ORDERDETAIL_ID, #{val.warehouseCount} TOTAL_WAREHOUSE_COUNT, #{val.totalWarehouseAmount} TOTAL_WAREHOUSE_AMOUNT, #{val.compRatio} COMP_RATIO
) b on a.ORDERDETAIL_ID = b.ORDERDETAIL_ID SET a.TOTAL_WAREHOUSE_COUNT = a.TOTAL_WAREHOUSE_COUNT + b.TOTAL_WAREHOUSE_COUNT, a.TOTAL_WAREHOUSE_AMOUNT = a.TOTAL_WAREHOUSE_AMOUNT + b.TOTAL_WAREHOUSE_AMOUNT, a.COMP_RATIO = a.COMP_RATIO + b.COMP_RATIO/100, a.CONFIRM_STATE = CASE WHEN a.TOTAL_WAREHOUSE_COUNT + b.TOTAL_WAREHOUSE_COUNT = a.PURCHASE_COUNT THEN 6 ELSE 5 END where (a.TOTAL_WAREHOUSE_COUNT + b.TOTAL_WAREHOUSE_COUNT) > 0 AND (a.TOTAL_WAREHOUSE_COUNT + b.TOTAL_WAREHOUSE_COUNT) <= a.PURCHASE_COUNT

mapper.select:
SELECT t.ADD_TIME "addTime", sum(t.PURCHASE_AMOUNT) "purchaseAmount", sum(t.TOTAL_WAREHOUSE_AMOUNT) "totalwarehouseAmount", avg(t.COMP_RATIO*100) "compRatio" FROM YIMIAO_ORDERDETAIL t WHERE 1=1
AND t.ADD_TIME >= #{starttime}
AND t.ADD_TIME <= #{endtime}
GROUP BY DATE_FORMAT(t.ADD_TIME,'%Y-%m') ORDER BY t.ADD_TIME DESC

SELECT t.ADD_TIME "addTime", sum(t.PURCHASE_AMOUNT) "purchaseAmount", sum(t.TOTAL_WAREHOUSE_AMOUNT) "totalwarehouseAmount", avg(t.COMP_RATIO*100) "compRatio", t.HOSPITAL_NAME, t.HOSPITAL_ID FROM YIMIAO_ORDERDETAIL t WHERE 1=1
AND t.ADD_TIME >= #{starttime}
AND t.ADD_TIME <= #{endtime}
AND t.HOSPITAL_NAME LIKE CONCAT(CONCAT('%', #{hospName}), '%')
GROUP BY HOSPITAL_ID,DATE_FORMAT(t.ADD_TIME, '%Y-%m') ORDER BY t.ADD_TIME DESC

SELECT t.ADD_TIME "addTime", sum(t.PURCHASE_AMOUNT) "purchaseAmount", sum(t.TOTAL_WAREHOUSE_AMOUNT) "totalwarehouseAmount", avg(t.COMP_RATIO*100) "compRatio", t.COMPANY_NAME_SC, t.COMPANY_ID_SC FROM YIMIAO_ORDERDETAIL t WHERE 1=1
AND t.ADD_TIME >= #{starttime}
AND t.ADD_TIME <= #{endtime}
AND t.COMPANY_NAME_SC LIKE CONCAT(CONCAT('%', #{prodCompName}), '%')
GROUP BY COMPANY_ID_SC,DATE_FORMAT(t.ADD_TIME, '%Y-%m') ORDER BY t.ADD_TIME DESC

SELECT t.ADD_TIME "addTime", sum(t.PURCHASE_AMOUNT) "purchaseAmount", sum(t.TOTAL_WAREHOUSE_AMOUNT) "totalwarehouseAmount", avg(t.COMP_RATIO*100) "compRatio", t.COMPANY_NAME_PS FROM YIMIAO_ORDERDETAIL t WHERE 1=1
AND t.ADD_TIME >= #{starttime}
AND t.ADD_TIME <= #{endtime}
AND t.COMPANY_NAME_PS LIKE CONCAT(CONCAT('%', #{delCompName}), '%')
GROUP BY COMPANY_NAME_PS,DATE_FORMAT(t.ADD_TIME, '%Y-%m') ORDER BY t.ADD_TIME DESC

SELECT t.ADD_TIME, sum(t.PURCHASE_AMOUNT) "purchaseAmount", sum(t.TOTAL_WAREHOUSE_AMOUNT) "totalwarehouseAmount", avg(t.COMP_RATIO * 100) "compRatio", t.GOODS_NAME, t.GOODS_ID, t.PRODUCT_NAME, t.OUTLOOK, t.MEDICINEMODEL, t.COMPANY_NAME_SC, t.COMPANY_NAME_TB FROM YIMIAO_ORDERDETAIL t WHERE 1=1
AND t.ADD_TIME >= #{starttime}
AND t.ADD_TIME <= #{endtime}
AND t.GOODS_NAME LIKE CONCAT(CONCAT('%', #{drugName}), '%')
GROUP BY GOODS_ID, DATE_FORMAT(t.ADD_TIME, '%Y-%m') ORDER BY t.ADD_TIME DESC

SELECT t.GOODS_ID '产品编号', t.GOODS_NAME '产品名称', t.PRODUCT_NAME '疫苗通用名', t.UNIT '最小制剂单位', concat(case when t.OUTLOOK is null then "" else t.OUTLOOK end,' (',case when t.MEDICINEMODEL is null then "" else t.MEDICINEMODEL end,')') "制剂规格（申报剂型）", concat(case when t.COMPANY_NAME_SC is null then "" else t.COMPANY_NAME_SC end,' (',case when t.COMPANY_NAME_TB is null then "" else t.COMPANY_NAME_TB end,')') "生产企业（投标企业）", t.HOSPITAL_NAME '疾控中心', t.ORDER_NAME '订单名称', t.COMPANY_NAME_SC '生产企业', t.COMPANY_NAME_PS '配送企业', t.PURCHASE_PRICE '最小制剂单位中标价格（元）', sum(t.PURCHASE_AMOUNT) '采购金额(元)', sum(t.TOTAL_WAREHOUSE_AMOUNT) '入库金额(元)', FORMAT(avg(t.COMP_RATIO * 100),2) '入库率(%)', DATE_FORMAT(t.ADD_TIME, '%Y-%m') '月度', s.AREA_NAME '所属地区' FROM yimiao_orderdetail t LEFT JOIN std_hospital s ON t.HOSPITAL_ID = s.HOSPITAL_ID WHERE DATE_FORMAT(t.ADD_TIME, '%Y-%m') = #{date}
AND t.HOSPITAL_ID = #{hospCode}
AND t.COMPANY_ID_SC = #{prodCode}
AND t.GOODS_ID = #{drugCode}
AND t.ADD_TIME >= #{starttime}
AND t.ADD_TIME <= #{endtime}
AND t.HOSPITAL_NAME LIKE CONCAT(CONCAT('%', #{hospName}), '%')
AND t.COMPANY_NAME_SC LIKE CONCAT(CONCAT('%', #{prodCompName}), '%')
AND t.COMPANY_NAME_PS LIKE CONCAT(CONCAT('%', #{delCompName}), '%')
AND t.GOODS_NAME LIKE CONCAT(CONCAT('%', #{drugName}), '%')
GROUP BY
t.HOSPITAL_ID
t.GOODS_ID
t.COMPANY_NAME_PS
t.COMPANY_ID_SC
ORDER BY t.ADD_TIME DESC
