mapper.update:
UPDATE YIMIAO_ORDERDETAIL_RET a INNER JOIN (
select #{val.orderdetailRetId} ORDERDETAIL_RET_ID, #{val.retCount} TOTAL_RETURN_COUNT
) b on a.ORDERDETAIL_RET_ID = b.ORDERDETAIL_RET_ID SET a.TOTAL_RETURN_COUNT = b.TOTAL_RETURN_COUNT, a.TOTAL_RETURN_AMOUNT =a.PURCHASE_PRICE*b.TOTAL_RETURN_COUNT

UPDATE YIMIAO_ORDERDETAIL_RET a INNER JOIN (
select #{val.orderdetailRetId} ORDERDETAIL_RET_ID, #{val.orderdetailState} ORDERDETAIL_STATE, #{val.refuseReason} RETURN_REASON
) b on a.ORDERDETAIL_RET_ID = b.ORDERDETAIL_RET_ID SET a.ORDERDETAIL_STATE = b.ORDERDETAIL_STATE, a.REFUSE_REASON =b.RETURN_REASON

UPDATE yimiao_orderdetail c INNER JOIN ( SELECT a.ORDERDETAIL_ID ORDERDETAIL_ID, SUM(a.TOTAL_RETURN_COUNT) TOTAL_RETURN_COUNT FROM(
select #{val.orderDetailId} ORDERDETAIL_ID, #{val.totalReturnCount} TOTAL_RETURN_COUNT
) a GROUP BY a.ORDERDETAIL_ID ) d on c.ORDERDETAIL_ID = d.ORDERDETAIL_ID SET c.TOTAL_RETURN_COUNT = c.TOTAL_RETURN_COUNT - d.TOTAL_RETURN_COUNT

mapper.select:
SELECT COUNT(*) FROM ( SELECT e.ORDERDETAIL_RET_ID ORDERDETAIL_RET_ID, e.ORDERDETAIL_ID ORDERDETAIL_ID, sum(e.TOTAL_RETURN_COUNT) TOTAL_RETURN_COUNT FROM yimiao_orderdetail_ret e WHERE ORDERDETAIL_RET_ID IN
#{var.orderdetailRetId}
GROUP BY ORDERDETAIL_ID ) d,yimiao_orderdetail a INNER JOIN ( SELECT c.ORDERDETAIL_ID ORDERDETAIL_ID, sum(c.TOTAL_RETURN_COUNT) TOTAL_RETURN_COUNT FROM (
SELECT #{var.orderDetailId} ORDERDETAIL_ID,#{var.retCount} TOTAL_RETURN_COUNT
) c GROUP BY ORDERDETAIL_ID ) b on a.ORDERDETAIL_ID = b.ORDERDETAIL_ID WHERE b.TOTAL_RETURN_COUNT - d.TOTAL_RETURN_COUNT > a.TOTAL_WAREHOUSE_COUNT - a.TOTAL_RETURN_COUNT
